mapper.update:
update jh_admin
set admin_name     = #{adminName,jdbcType=VARCHAR},
    admin_password = #{adminPassword,jdbcType=VARCHAR}
where admin_id = #{adminId,jdbcType=INTEGER}

mapper.select:
select admin_id, admin_name, admin_password
from jh_admin
where admin_id = #{adminId,jdbcType=INTEGER}

